import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

function Navbar() {
  const [connected, setConnected] = useState(false);
  const location = useLocation();
  const [currAddress, updateAddress] = useState('0x');

  useEffect(() => {
    async function checkMetamaskConnection() {
      if (window.ethereum) {
        try {
          const chainId = await window.ethereum.request({ method: 'eth_chainId' });
          if (chainId === '0x6b') { // Sepolia chain ID
            const accounts = await window.ethereum.request({ method: 'eth_accounts' });
            if (accounts.length > 0) {
              setConnected(true);
              const address = accounts[0];
              updateAddress(address);
            } else {
              setConnected(false);
            }
          } else {
            setConnected(false);
            // Optionally, you can inform the user to switch to the Sepolia network.
          }
        } catch (error) {
          setConnected(false);
          console.error(error);
        }
      } else {
        setConnected(false);
        // Optionally, you can inform the user to install Metamask.
      }
    }

    checkMetamaskConnection();
  }, []);

  async function connectWebsite() {
    if (connected) {
      // Perform actions that require a connected wallet.
      // You can call your own functions here.
    } else {
      // Optionally, you can inform the user to connect their wallet.
    }
  }

  return (
    <div className="navbar">
      <nav className="w-screen">
        <ul className='flex items-end justify-between py-3 bg-transparent text-white pr-5'>
          {/* ... Rest of your Navbar content ... */}
          <li>
            <button
              className="enableEthereumButton bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded text-sm"
              onClick={connectWebsite}
              disabled={!connected}
            >
              {connected ? "Connected" : "Connect Wallet"}
            </button>
          </li>
        </ul>
      </nav>
      <div className='text-white text-bold text-right mr-10 text-sm'>
        {connected
          ? `Connected to: ${currAddress.substring(0, 15)}...`
          : 'Not Connected. Please login to view NFTs'}
      </div>
    </div>
  );
}

export default Navbar;


  
